import React, { useState } from 'react';
import styled from 'styled-components';
import { Grid } from '@material-ui/core';
import CareerCard from '../../component/CareerCard';
import { jobs } from '../../data/infos';

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
const Section = styled.div`
  margin: 20px 0px;
  align-self: center;
  .desc {
    font-size: smaller;
    padding-left: 4px;
  }
`;
const CardContainer = styled.div`
  display: flex;
`;
const Filter = styled.div`
  display:flex;

`;
const Chip = styled.div`
  width: auto;
  height: auto;
  padding: 6px 12px;
  margin: 4px 8px;
  border-radius: 4px;
  background-color: #98b9ce1a;
`;
const getCareerCards = (jobs) => {
  return jobs.map((item) => {
    return (
      <CardContainer key={item.id}>
        <CareerCard item={item} />
      </CardContainer>
    );
  });
};

const CareersContainer = () => {
  const [filter, setFiler] = useState();
  return (
    <Wrapper>
      <Section>
        <div className="desc">*기여도가 높고 서비스에 영향을 많이 끼친 프로젝트 위주로 정리하였습니다.</div>
        <Filter>
          <Chip>전체</Chip>
          <Chip>#FE</Chip>
          <Chip>#BE</Chip>
        </Filter>
        {getCareerCards(jobs)}
      </Section>
    </Wrapper>
  );
};

export default CareersContainer;
