import React from 'react';
import styled from 'styled-components';

import { colorDict } from '../helpers/colors';
import HistorySummary from '../data/img/이력요약.png';

const Chip = styled.div`
  width: auto;
  height: auto;
  padding: 6px 12px;
  margin: 4px 8px;
  border-radius: 4px;
  // background-color: ${colorDict.lightBackground};
  background-color: #98b9ce1a;

`;

const CustomCard = styled.div`
  width: 860px;
  margin: 20px 0px;
  display: flex;
  flex-direction: column;
  height: auto;
  justify-content: space-between;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;

  .desc {
    margin: 6px 0px;
  }
  .techList {
    display: flex;
    flex-direction: row;
    flex-flow: wrap;
  }
`;

const SummarySection = styled.div`
  display: flex;
  padding: 20px;
  border-radius: 4px;
  background-color: #98b9ce1a;

  .info {
    display: flex;
    flex-direction: column;
    margin-left: 20px;

    .company {
      font-size: larger;
      font-weight: bold;
      .link {
        margin-left: 6px;
        font-size: smaller;
        color: ${colorDict.lightText};
        &:hover {
          cursor: pointer;
          color: ${colorDict.darkText};
        }
      }
    }
    .date {
      color: ${colorDict.lightText};
    }
    .summary {
      margin-top: 10px;
      white-space: break-spaces;
      word-break: keep-all;
    }
  }
`;

const ProjectSection = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const ImgWrapper = styled.div`
  position: relative;
  overflow: hidden;
  width: 200px;
`;

const Img = styled.img`
  max-width: 100%;
  background-color: #ffffff;
  border-radius: 4px;
`;

const BoxWrapper = styled.div`
  disply: flex;
  flex-display: column;
  border-left: solid 2px ${colorDict.darkText};
  padding-left: 10px;
  margin: 12px 0px;

  .title {
    display: flex;
    flex-direction: row;
    font-weight: bold;
    .sub {
      margin-left: 10px;
      font-weight: normal;
      color: ${colorDict.lightText};
    }
  }
  .content {
    margin: 10px 0px;
    white-space: pre-wrap;
  }
  .chipContainer {
    width: 100%;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    font-size: smaller;
  }
  .link {
    font-size: smaller;
    color: ${colorDict.lightText};
    &:hover {
      cursor: pointer;
    }
  }
`;

const ImgContainer = styled.div`
  width: 100%;
  margin: 10px 0px;
  display: flex;
  flex-direction: row;
  justify-content: space-around;

  .imgWrapper {
    border: solid 1px ${colorDict.lightText50};
    padding: 5px;
    margin: 10px;
    overflow: hidden;
    display: flex;
  }
`;

const getChips = (items) => {
  return items.map((item, index) => {
    return <Chip key={index}>{item}</Chip>;
  });
};

const getProjectBox = (projects) => {
  return projects.map((item, index) => {
    return (
      <BoxWrapper key={index}>
        <div className="title">
          {item.title}
          <div className="sub">
            {item.boundary.map((item, index) => {
              return <span key={index}>#{item} </span>;
            })}
          </div>
        </div>
        <p className="content">{item.content}</p>
        {item.url && (
          <a className="link" href={item.url} target="_blank" rel="noreferrer">
            {item.url}
          </a>
        )}
        <div className="chipContainer">{getChips(item.tech)}</div>
        {item.preview && (
          <ImgContainer>
            <div className="imgWrapper">
              <Img src={item.preview[0]} alt="preview" />
            </div>
            <div className="imgWrapper">
              <Img src={item.preview[1]} alt="preview" />
            </div>
          </ImgContainer>
        )}
      </BoxWrapper>
    );
  });
};

const CareerCard = (props) => {
  const { item: careers } = props;
  const { logo, company, projects, date, url, summary } = careers;

  return (
    <CustomCard key={careers.id}>
      <ContentWrapper>
        <SummarySection>
          <ImgWrapper>
            <Img src={logo} alt="logo" />
          </ImgWrapper>
          <div className="info">
            <span className="company">
              {company}
              <a className="link" href={url} target="_blank" rel="noreferrer">
                {url}
              </a>
            </span>
            <span className="date">{date}</span>
            <div className="summary">{summary}</div>
          </div>
        </SummarySection>
        <ProjectSection>{getProjectBox(projects)}</ProjectSection>
      </ContentWrapper>
    </CustomCard>
  );
};

export default CareerCard;
